/**
 * Функция для бинарного поиска в отсортированном по возрастанию массиве
 * Работает аналогично Array.prototype.indexOf.
 * @param arr - массив, в котором мы ищем элемент.
 * @param item - элемент, который мы ищем.
 * @param start - индекс, с котрого начинается поиск.
 * @param end - индекс, которым заканчивается поиск.
 * @return Функция возвращает -1, если значения нет в массиве, либо возвращает индекс элемента.
 */
function binarySearch(arr, item, start = 0, end = arr.length - 1) {
    if (arr.__proto__ != Array.prototype)
        return -1;
    if (typeof item != "number" || typeof start != "number" || typeof end != "number")
        return -1;
    if (end < start || start < 0)
        return -1;
    let mid = start + Math.floor((end - start) / 2);
    if (arr[mid] === item)
        return mid;
    if (start === end)
        return -1;
    if (arr[mid] > item)
        return binarySearch(arr, item, start, mid - 1);
    if (arr[mid] < item)
        return binarySearch(arr, item, mid + 1, end);
}
export default binarySearch;