/**
 * Функция, которая находит максимальное количество различных натуральных слагаемых числа num.
 * @param num - число, слагаемые которого нужно найти.
 * @returns Возвращает слагаемые числа.
 */
function findMaxSum(num) {
    let terms = [], i = 1;
    while (i <= num)
    {
        if (!terms.includes(i) && !terms.includes(num - i) && i !== num - i)
        {
            terms.push(i);
            num -= i;
            i++;
        }
        else
        {
            terms.push(num);
            break;
        }
    }
    return (terms);
}

export default findMaxSum;