/**
 * Функция принимает на вход массив отрезков на прямой, длиной len,
 * а возвращает минимальное множество точек, которые лежат на всех заданных отрезках.
 * @param len - длина массива points (количество отрезков).
 * @param points - массив отрезков.
 * @return Возвращает массив точек.
 */
function findMinPoints(len, points)
{
    points = points.sort((a, b) => (a[1] !== undefined && b[1] !== undefined) ? a[1] - b[1] : 0);
    let i = 0, j = 0, result = [points[i++][1]];
    while (i < len)
    {
        if (points[i][0] > result[j] && i + 1 !== len)
            result[++j] = points[i][1];
        else if (points[i][0] > result[j])
            result[++j] = points[i][0];
        i++
    }
    return (result);
}

export default findMinPoints;