/**
 * Функция восстанавливает строку по её коду и беспрефиксному коду символов.
 * @param code - битовый код.
 * @param letterToCode - объект вида {буква: код}.
 * @return Возвращает восстановленную строку.
 */
function huffmanCodeToStr(code, letterToCode) {
    let resultStr = "", arrLetterCode = [], i = 0, j = 0;

    for (letter in letterToCode)
        arrLetterCode.push({letter, code: letterToCode[letter]});
    arrLetterCode.sort((a, b) => a.code.length - b.code.length);
    while (j < arrLetterCode.length)
    {
        if (code.startsWith(arrLetterCode[j].code, i))
        {
            resultStr += arrLetterCode[j].letter;
            i += arrLetterCode[j].code.length;
            j = 0;
        }
        else
            j++;
    }
    return (resultStr);
}

export default huffmanCodeToStr;
