/**
 * Функция быстрой сортировки массива.
 * Предполагается, что массив содержит только числа.
 * Не изменяет входной массив, а возвращает новый, с отсортированными элементами (чистая функция).
 * @param arr - массив, который нужно отсортировать.
 * @returns {*} - массив, с отсортированными элементами.
 */
function quickSort(arr) {
    if (arr.__proto__ !== Array.prototype)
        return [];
    const elemIdx = 0;
    if (arr.length < 2)
        return [...arr];
    return [...quickSort(arr.filter((item, idx)=> item <= arr[elemIdx] && idx !== elemIdx)),
            arr[elemIdx],
            ...quickSort(arr.filter((item, idx)=> item > arr[elemIdx]))]
}

export default quickSort;