/**
 * Функция сортировки массива выбором.
 * Предполагается, что в массиве содержатся только числа.
 * Функция не изменяет входной массив, а возвращает новый (чистая функция).
 * @param arr - массив, который нужно отсортировать.
 * @returns {Array} - отсортированный массив.
 */
function selectionSort(arr) {
    if (arr.__proto__ != Array.prototype)
        return [];
    let min;
    const inputArray = Array.from(arr);
    const newArray = [];
    while (inputArray.length)
    {
        min = inputArray.reduce((min, item) => item < min ? item : min, inputArray[0]);
        inputArray.splice(inputArray.indexOf(min), 1);
        newArray.push(min);
    }
    return newArray;
}

export default selectionSort;